import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchName, updateStatus } from "../actions/actions";

function Profile() {
  const { name, age, status } = useSelector((state) => state);
  const dispatch = useDispatch();
  const updateAge = (age) => {
    dispatch({ type: "UPDATE_AGE", payload: age });
  };
  const updateName = async (name) => {
    dispatch(fetchName(name));
  };
  const changeStatus = (status) => {
    dispatch(updateStatus(status));
  };
  return (
    <div>
      <h1>I am {name}</h1>
      <h1>My age is {age}</h1>
      <h1>My status is {status}</h1>
      <button onClick={() => updateAge(30)}>Update Age</button>
      <button onClick={() => updateName("Abuzar")}>Update Name</button>
      <button onClick={() => changeStatus("Single")}>Update Status</button>
    </div>
  );
}

export default Profile;
